package com.syyamnoor.mytimestopstories.utils

import com.syyamnoor.mytimestopstories.domain.models.News
import com.syyamnoor.mytimestopstories.domain.models.NewsImage
import java.util.*
import kotlin.random.Random


object Utils {

    fun createRandomNews(): News {
        val titles = listOf(
            "Syyam", "Another Title", "My Title", "Whose Title",
            "Another Title", "Get Your title", "Be for Titles", "I have no ideas"
        )
        val newsAbstracts =
            titles.map { "$it abstract" }.shuffled()
        val calendar = Calendar.getInstance()
        val dates =
            (1..30).map { calendar.set(Calendar.DAY_OF_MONTH, it); calendar.time }.shuffled()
        val categories = listOf(
            "arts", "automobiles", "books", "business", "fashion", "food",
            "health", "insider", "magazine"
        ).shuffled()
        val authors = listOf(
            "Syyam",
            "Syyam",
            "Syyam",
            "Syyam",
            "Syyam",
            "Syyam",
            "Syyam",
            "Syyam"
        ).map { "By $it" }.shuffled()
        val url = "https://www.prnewswire.com/ae/news-releases/egym-presents-corona-gym-solution-a-solution-package-for-the-successful-re-opening-of-fitness-studios-894687861.html"

        val images = (1 until authors.size)
            .map {
                NewsImage(
                    "$it ${authors[it]}", "${it - 1} ${authors[it - 1]}",
                    "data:image/jpeg;base64,"
                )
            }.shuffled()

        return News(
            15,
            titles[Random.nextInt(titles.size)],
            newsAbstracts[Random.nextInt(newsAbstracts.size)],
            categories[Random.nextInt(categories.size)],
            dates[Random.nextInt(dates.size)],
            categories[Random.nextInt(categories.size)],
            authors[Random.nextInt(authors.size)],
            url,
            listOf(images[Random.nextInt(images.size)])
        )
    }
}